import { Keyboard, StyleSheet, TouchableWithoutFeedback } from 'react-native';
import { useState } from 'react';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import { RootTabScreenProps } from '../types';

import { Button, Divider, Flex, TextInput, Pressable } from "@react-native-material/core";

export default function Login() {
  const [password, setPassword] = useState<string>('');

  return (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
      <View style={styles.container}>
        <TextInput label='Email' style={styles.input} variant='outlined' color='#0089E3' />
        <TextInput label='Password' style={styles.input} variant='outlined' color='#0089E3' onChangeText={(value) => setPassword(() => value)} />
        <Pressable style={styles.forgotPasswordContainer}>
          <Text style={styles.forgotPasswordLink}>Forgot password?</Text>
        </Pressable>
        <Button title="Log In" color="#0089E3" titleStyle={styles.loginButtonText} pressableContainerStyle={styles.loginButton} disabled={!password.length}/>
        <Flex style={styles.signUpContainer}>
          <Text>Don't have an account?</Text>
          <Pressable>
            <Text style={styles.signUpLink}>{' '}Sign up here.</Text>
          </Pressable>
        </Flex>
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // alignItems: 'center',
    justifyContent: 'center',
    padding: 45
  },
  input: {
    marginBottom: 20
  },
  signUpContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    marginTop: 20
  },
  signUpLink: {
    color: '#0089E3'
  },
  loginButton: {
    padding: 5
  },
  loginButtonText: {
    color: 'white',
  },
  forgotPasswordContainer: {
    alignSelf: 'flex-end'
  },
  forgotPasswordLink: {
    color: '#0089E3',
    fontWeight: '700'
  }
});
