import { Keyboard, StyleSheet, TouchableWithoutFeedback } from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import { RootTabScreenProps } from '../types';

import { Button, Divider, Flex, TextInput, Pressable } from "@react-native-material/core";

export default function Login() {
  return (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
      <View style={styles.container}>
        <TextInput label='Email' style={styles.input} variant='outlined' color='#0089E3'/>
        <TextInput label='Password' style={styles.input} variant='outlined' color='#0089E3'/>
        <Pressable><Text style={styles.forgotPasswordLink}>Forgot password?</Text></Pressable>
        <Button title="Log In" color="#0089E3" titleStyle={styles.loginButton}/>
        <Flex style={styles.signUpContainer}>
          <Text>Don't have an account?</Text>
          <Pressable><Text style={styles.signUpLink}>{' '}Sign up here.</Text></Pressable>
        </Flex>
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // alignItems: 'center',
    justifyContent: 'center',
    padding: 45
  },
  input: {
    marginBottom: 20
  },
  signUpContainer: {
    flexDirection: 'row'
  },
  signUpLink: {
    color: '#0089E3'
  },
  loginButton: {
    color: 'white'
  },
  forgotPasswordLink: {
    color: '#0089E3',
    fontWeight: '600'
  }
});
